if($show) {
	// Make sure the file exists and is writeable
	$exists = at("Yes");
	$writeable = at("Yes");
	$file = getBasePath() . '/robots.txt';

	if(!file_exists($file)) {
		$exists = at("No");
	}

	if(!is_writeable($file)) {
		$writeable = at("No");
	}

	// Show description
	$message = at("<br />File Location: <b>{file}</b>.<br /> File Exists: <b>{exists}</b>. File Writable: <b>{writeable}</b>", array('{file}' => $file, '{exists}' => $exists, '{writeable}' => $writeable));
	$setting->description = str_replace('{message}', $message, $setting->description);
}

// Save contents
if($store) {
	$file = getBasePath() . '/robots.txt';
	// Make sure the file exists and is writeable
	// If it doesnt exists try to create it
	if(!file_exists($file)) {
		$fileSaved = @file_put_contents($file, '');
		if(!$fileSaved) {
			ferror(at("Sorry, We could not save the file {file}", array('{file}' => $file)));
		}
	} else {
 		// Is it writeable
		if(is_writeable($file)) {
			$fileSaved = file_put_contents($file, $setting->value);
		} else {
			if(!@chmod($file, 0777)) {
				ferror(at("Sorry, The file {file} is not writeable.", array('{file}' => $file)));
			} 
		}
	}
}

if($store) {
	Yii::app()->cache->flush();
}

if($show) {
	$cacheOptions = array();
	foreach(getSupprotedCacheOptions() as $cacheKey => $cacheData) {
		$status = $cacheData['visible'] ? at('Available') : at('Not Available');
		$cacheOptions[] = at("{title} is <b>{status}</b>", array('{title}' => $cacheData['title'], '{status}' => $status));
	}

	$setting->description .= '<br /><br />' . implode('<br />', $cacheOptions);
}

if($store) {
	Yii::app()->dataCache->flush();
}

if($show) {
	$cacheOptions = array();
	foreach(getSupprotedCacheOptions() as $cacheKey => $cacheData) {
		$status = $cacheData['visible'] ? at('Available') : at('Not Available');
		$cacheOptions[] = at("{title} is <b>{status}</b>", array('{title}' => $cacheData['title'], '{status}' => $status));
	}

	$setting->description .= '<br /><br />' . implode('<br />', $cacheOptions);
}

if($show) {
	if(!@class_exists('Memcache')) {
		$setting->disabled = true;
		$setting->description .= at("<br /><b>Memcache is not loaded.</b>");
	}
}

